import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom'
import {  updateUserProfile } from '../actions/userAction'
import { Row, Col, Form, Button, Table } from 'react-bootstrap'
import Message from '../components/Message'
import Loader  from '../components/Loader'
import {LinkContainer} from 'react-router-bootstrap'
import { USER_UPDATE_PROFILE_RESET } from '../constants/userConstants';
import { listMyOrders } from '../actions/orderAction';

function Profilepage() {

    const [name, setName] =useState('')
    const [email, setEmail] =useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [message, setMessage] = useState('')

    const dispatch = useDispatch()
    const history = useNavigate()


    const userDetails = useSelector(state=>state.userDetails)
    const { error, loading, user } = userDetails
    console.log(userDetails)
    
    const userLogin = useSelector(state=>state.userLogin)
    const { userInfo } = userLogin
    console.log(userLogin)

    const userUpdateProfile = useSelector(state=>state.userUpdateProfile)
    const { success } = userUpdateProfile
    console.log(userUpdateProfile)

    const orderListMy = useSelector(state=>state.orderListMy)
    const { error:errorOrders, loading:loadingOrders, orders } = orderListMy

    useEffect(() => {
        if (!userInfo) {
            history('/login')
        } else {
            if (!user || !user.name || success || userInfo._id !== user._id) {
                dispatch({ type: USER_UPDATE_PROFILE_RESET })
                dispatch(getUserDetails('profile')) // this line is causing infinite loop
                dispatch(listMyOrders) 

            } else {
                setName(user.name)
                setEmail(user.email)
            }
        }
    }, [dispatch, history, userInfo, user, success])

    const submitHandler = (e)=>{
        e.preventDefault()

        if(password!==confirmPassword){
            setMessage('Passwords do not match.')
        }
        else
        {
            dispatch(updateUserProfile({
                'id': user._id,
                'name': name,
                'email': email,
                'password':password
            }))
            setMessage('')
        }
    }

     return (
    <div>
        <Row>
            <Col md={4}>
                <h2 className='product-title'>My Profile</h2>
                {message && <Message variant='danger'>{error}</Message>}
                {loading && <Loader/>}
                <Form onSubmit={submitHandler}>
                    <Form.Group controlId='name'>
                        <Form.Label>Username</Form.Label>
                        <Form.Control
                         required 
                        type='name'
                        placeholder='Enter your new username'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        
                        ></Form.Control>


                    </Form.Group>

                    <Form.Group controlId='email'>
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                         required 
                        type='email'
                        placeholder='Enter your new email'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        
                        ></Form.Control>


                    </Form.Group>

                    <Form.Group controlId='password'>
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                         required 
                        type='password'
                        placeholder='Enter your new password'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}                     
                        ></Form.Control>
                </Form.Group>

                    <Form.Group controlId='passwordConfirm'>
                        <Form.Label>Confirm password</Form.Label>
                        <Form.Control
                         required 
                        type='password'
                        placeholder='Confirm your new password'
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}                      
                        ></Form.Control>
                    </Form.Group>
                    <br></br><Button type='submit' variant='warning'>Update</Button>
                </Form>           
                 </Col>
                 
                 <Col md={8}>
                     <h2 className='product-title'>My Orders</h2>
                     {loadingOrders ? (<Loader />) :
                         errorOrders ? (<Message variant='danger'>{errorOrders}</Message>) :
                             (
                                 <Table striped responsive className='table-sm'>
                                     <thead>
                                         <tr>
                                             <th>ID</th>
                                             <th>Date</th>
                                             <th>Total Cost</th>
                                             <th>Payment Status</th>
                                             <th>Details</th>


                                         </tr>
                                     </thead>

                                     <tbody>
                                         {orders.map(order => (
                                             <tr key={order._id}>
                                                 <td>{order._id}</td>
                                                 <td>{order.createAt}</td>
                                                 <td>{order.TotalPrice}</td>
                                                 <td>{order._isPaid ? order.paidAt : (<i className='fa fa-times' style={{ color: 'red' }}></i>)}</td>
                                                 <td>
                                                     <LinkContainer to={`/order/${order._id}`}>
                                                         <Button> Details</Button>
                                    
                                                     </LinkContainer>


                                                 </td>


                                             </tr>


                                         ))}
                                     </tbody>


                                 </Table>
                             )
                     }
                 </Col>
          </Row>
            </div>
  )
}

export default Profilepage
